<?phpnamespace backend\models;use Yii;/** * This is the model class for table "{{%user_invoice}}". * * @property integer $id * @property integer $type * @property integer $user_id * @property string $company_name * @property string $number * @property string $phone * @property string $bank * @property string $bank_account */class UserInvoice extends \yii\db\ActiveRecord{    /**     * @inheritdoc     */    public static function tableName()    {        return '{{%user_invoice}}';    }    public static $type=[        1=>'增值税发票',        2=>'电子普通发票',        3=>'普通发票'    ];    /**     * @inheritdoc     */    public function rules()    {        return [            [['type', 'user_id','is_default'], 'integer'],            [['user_id', 'company_name', 'number', 'phone', 'bank', 'bank_account','address','title'], 'required'],            [['company_name', 'bank', 'bank_account','address','email'], 'string', 'max' => 255],            [['number'], 'string', 'max' => 50],            [['phone'], 'string', 'max' => 20],        ];    }    /**     * @inheritdoc     */    public function attributeLabels()    {        return [            'id' => 'ID',            'type' => '发票类型',            'user_id' => '用户ID',            'company_name' => '单位名称',            'number' => '识别号',            'phone' => '注册电话',            'bank' => '开户银行',            'bank_account' => '银行账户',            'is_default'=>'默认发票',            'address'=>'地址',            'title'=>'发票抬头',            'email'=>'邮箱'        ];    }}